import { createSlice } from '@reduxjs/toolkit';


const initialState = {
    currentUser: null,
    loading: false,
    error: false,
};

const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {
        signInStart: (state) => {
            state.loading = true;
        },
        signInSuccess: (state,action) => {
            state.currentUser = action.payload;
            state.loading = false;
            state.error = false;
        },
        signInFailure: (state,action) => {
            state.loading = false;
            state.error = action.payload;
        },
        UpdateStart: (state) => {
            state.loading = true;
        },
        UpdateSuccess: (state,action) => {
            state.currentUser = action.payload;
            state.loading = false;
            state.error = false;
        },
        UpdateFailure: (state,action) => {
            state.loading = false;
            state.error = action.payload;
        },
        DeleteStart: (state) => {
            state.loading = true;
        },
        DeleteSuccess: (state) => {
            state.currentUser = null;
            state.loading = false;
            state.error = false;
        },
        DeleteFailure: (state,action) => {
            state.loading = false;
            state.error = action.payload;
        },
        SignOut: (state) => {
            state.currentUser = null;
            state.loading = false;
            state.error = false;
        },
    }
})

export const { signInStart, signInSuccess, signInFailure, UpdateStart, UpdateSuccess, UpdateFailure, DeleteStart, DeleteSuccess, 
    DeleteFailure, SignOut} = userSlice.actions;

export default userSlice.reducer;
