import { useSelector } from "react-redux";
import { useEffect, useRef, useState } from "react";
import {getDownloadURL, getStorage, ref, uploadBytesResumable} from 'firebase/storage';
import app from "../firebase";

export default function Profile() {
  const {currentUser} = useSelector((state) => state.user); 
  const [image, setImage] = useState(undefined);
  const fileRef = useRef(null);
  const [imagePercentage, setImagePercentage] = useState(0);
  const [imageError,setImageError] = useState(false);
  const [formData,setFormData] = useState({});
  useEffect(() => {
    if(image){
      handleFileUpload(image);
    }
  }, [image]);

  const handleFileUpload = async (image) => {
    const storage = getStorage(app);
    const fileName = new Date().getTime() + image.name;
    const storageRef = ref(storage, fileName);
    const uploadTask =  uploadBytesResumable(storageRef, image);
    uploadTask.on(
      'state_changed',
      (snapshot) => {
        const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        setImagePercentage(Math.round(progress));

      },
    (error) => {
      setImageError(true);
    },
    () => {
      getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
        setFormData({...formData, profilePicture: downloadURL});
      })
    }
    );
  }

  const handleChange = (e) => {
    setFormData({...formData, [e.target.id]: e.target.value});
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${baseUrl}/user/update/`)
    } catch (error) {
      
    }
  }

  return (
    <div className=" p-3 max-w-lg mx-auto">
      <h1 className=" text-3xl font-semibold text-center my-7">Profile</h1>
      <form onSubmit={handleSubmit} className=" flex flex-col gap-4">
        <input type="file" ref={fileRef} hidden accept="image/*" 
        onChange={(e) => setImage(e.target.files[0])}/>
        <img 
          src={formData.profilePicture || currentUser.profilePicture} 
          alt="profile"
          onClick={() => fileRef.current.click()}
          className=" h-24 w-24 self-center cursor-pointer rounded-full object-cover mt-2" />
        <p className=" text-sm self-center"> 
          {imageError ? (
            <span className=" text-red-700">Error Upoading Image (File size must be less than 2 MB)</span>
          ):  imagePercentage > 0 && imagePercentage < 100 ? (
            <span className=" text-slate-700">{`Uploading: ${imagePercentage} %`}</span>) : imagePercentage === 100 ? (
              <span className=" text-green-700">Image Uploaded Successfully</span>) : ''
          }
          
        </p>
        <input defaultValue={currentUser.username} type="text" id="username" placeholder="Username" className=" bg-slate-100 rounded-lg p-3" 
        onChange={handleChange}/>
        <input defaultValue={currentUser.email} type="email" id="email" placeholder="Email" className=" bg-slate-100 rounded-lg p-3" 
        onChange={handleChange}/>
        <input type="password" id="password" placeholder="Password" className=" bg-slate-100 rounded-lg p-3" 
        onChange={handleChange}/>
        <buuton 
          className=' bg-slate-700 text-center cursor-pointer text-white p-3 rounded-lg uppercase hover:opacity-95 disabled:opacity-80'>
            Update
        </buuton>
      </form>
      <div className=" flex justify-between mt-5">
        <span className="text-red-600 cursor-pointer">Delete Account</span>
        <span className="text-red-600 cursor-pointer">Sign Out</span>
      </div>
    </div>
  )
}
